+  display: flex;
+}
+
+/* Cell Styling */
+.cell {
+  width: 40px;
+  height: 40px;
+  border: 1px solid #333;
+  display: flex;
+  align-items: center;
+  justify-content: center;
+  font-size: 20px;
+  cursor: pointer;
+}
+
+/* Revealed Cell Styling */
+.cell.revealed {
+  background-color: #d0d0d0;
+}
+
+/* Hover Effect for Cell */
+.cell:hover {
+  background-color: #e0e0e0;
+}
+
+/* Button Styling */
+button {
+  margin-top: 20px;
+  padding: 10px 20px;
+  font-size: 16px;
+  cursor: pointer;
+  background-color: #333;
+  color: #fff;
+  border: none;
+  border-radius: 5px;
+}
+
+button:hover {
+  background-color: #555;
+}

