commits.content:
.homepage {
    text-align: center;
  }

  .difficulty-section {
    display: flex;
    justify-content: center;
    gap: 20px;
    margin-top: 40px;
    flex-wrap: wrap;
  }

  .difficulty-card {
    text-align: center;
    width: 250px;
    text-decoration: none;
    color: inherit;
  }

  .difficulty-card img {
    width: 100%;
    height: auto;
    border: 2px solid #333;
    border-radius: 8px;
    transition: transform 0.2s ease;
  }

  .difficulty-card h3 {
    margin-top: 10px;
    font-size: 1.2em;
    color: #333;
  }

  .difficulty-card img:hover {
    transform: scale(1.05);
  }
